import './App.css';
import {character, treasure, landMarks, curses, verb} from "./arrays"


async function getData() {
    let decision = 2//Math.floor(Math.random() *3+1)
    let randomMonster = character.monster.M1_5[Math.floor(Math.random()*character.monster.M1_5.length)] 
    let randomNPC = character.npc[Math.floor(Math.random()*character.npc.length)]
    let numberOfMonsters
    let additionMonster = null

    if(decision === 1){
        //Just NPC
        const npc = await fetch("https://api.open5e.com/monsters/?challenge_rating=&armor_class=&type=&name="+randomNPC+"&document=&document__slug="
        ).then(result => result.json());

        console.log(npc.results[0].challenge_rating)//npc.results[0].challenge_rating
        return npc

    }else if(decision === 2){ 
        //Just Monster
        const monster = await fetch("https://api.open5e.com/monsters/?challenge_rating=&armor_class=&type=&name="+randomMonster+"&document=&document__slug="
        ).then(result => result.json());
        console.log(monster)//monster.results[0].challenge_rating
        if(monster.results[0].challenge_rating === "1/4"){
            numberOfMonsters = Math.floor(Math.random()*6+1)
            console.log(numberOfMonsters)
            additionMonster = null
        }else if(monster.results[0].challenge_rating === "1/2"){
            numberOfMonsters = Math.floor(Math.random()*2+1)
            console.log(numberOfMonsters)
            additionMonster = null
        }else if(monster.results[0].challenge_rating === "1"){
            numberOfMonsters = 1
            console.log(numberOfMonsters)
            additionMonster = true
        }
        if (additionMonster === true){
            const monster2nd = await fetch("https://api.open5e.com/monsters/?challenge_rating=&armor_class=&type=&name="+Math.floor(Math.random()*6)+"&document=&document__slug="
        ).then(result => result.json());
        }

        return monster

    } else if (decision === 3){
        //Both
        const monster = await fetch("https://api.open5e.com/monsters/?challenge_rating=&armor_class=&type=&name="+randomMonster+"&document=&document__slug="
        ).then(result => result.json());
        const npc = await fetch("https://api.open5e.com/monsters/?challenge_rating=&armor_class=&type=&name="+randomNPC+"&document=&document__slug="
        ).then(result => result.json());
        console.log(npc)
        console.log(monster)
        return monster + " " + npc

    }


}







function RandomMonster() {

    return (
      <div className='App-header'>
        <button className='App' onClick={getData}>1-5</button>
        <button className='App'>6-10</button>
        <button className='App'>11-15</button>

      </div>
    );
}




export default RandomMonster;
